import React, { useState } from 'react';

const CreateTask = () => {
  const [formData, setFormData] = useState({ name: '', description: '', dueDate: '', priority: 'Normal' });

  const handleChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = e => {
    e.preventDefault();
    console.log('Task Created:', formData);
    // Connect to backend and Firebase push logic here
  };

  return (
    <div className="container mt-4">
      <h2>Create a New Task üìù</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Task Name" onChange={handleChange} required className="form-control mb-2"/>
        <textarea name="description" placeholder="Description" onChange={handleChange} className="form-control mb-2"/>
        <input type="date" name="dueDate" onChange={handleChange} className="form-control mb-2"/>
        <select name="priority" onChange={handleChange} className="form-control mb-2">
          <option>Low</option>
          <option>Normal</option>
          <option>High</option>
        </select>
        <button type="submit" className="btn btn-primary">Save Task</button>
      </form>
    </div>
  );
};

export default CreateTask;
